{{Ü-Tabelle|1|G=|Ü-Liste=
: [{{Ü|de|Hausstand}}, {{Ü|de|Haus}};
}}
{{Ü-Tabelle|2|G=|Ü-Liste=
: [{{Ü|de|Hausstand}}, {{Ü|de|Haus}};{{Ü|de|Geschlecht}}, {{Ü|de|Familie}};
}}
{{Ü-Tabelle|3|G=|Ü-Liste=
: [{{Ü|de|Hausstand}}, {{Ü|de|Haus}};{{Ü|de|Dienerschaft}}
}}

